async function () {
  showModal('Modal1');
  if (tbl_wallets.tableData.length < 1) {
    closeModal('Modal1');
    return [];
  }
  if (tbl_wallets.selectedRowIndex < 0) {
    closeModal('Modal1');
    return [];
  }
  apis.dataPath = ("/custom/vstorage/data/published.kread.character.").concat(tbl_wallets.selectedRow.character);
  let response = await RpcGetWalletDataAPI.run();
  if (response.result.response.value === null) {
    closeModal('Modal1');
    return [{
      "value": response.result.response.log
    }];
  }
  apis.height = response.result.response.height;
  let data = response.result.response.value && JSON.parse(atob(response.result.response.value));
  let result = JSON.parse(data.value).values.map(v => ({
    value: apis.cleanTxt(v)
  })).map(kv => ({
    "value": JSON.parse(kv.value)
  }));
  console.log(result);
  closeModal('Modal1');
  return result;
}

async function () {
  if (searchInput.text.length > 0) {
    return [{
      character: searchInput.text
    }];
  }
  let response = await RpcGetWalletApi.run();
  apis.height = response.result.response.height;
  let children = response.result.response.value && JSON.parse(atob(response.result.response.value)).children;
  return children.map(v => ({
    character: v
  }));
}
