async function () {
  if (searchInput.text.length > 0) {
    return [{
      vault: searchInput.text
    }];
  }
  let response = await RpcGetWalletApi.run();
  vaultsApi.height = response.result.response.height;
  let children = response.result.response.value && JSON.parse(atob(response.result.response.value)).children;
  vaultsApi.listCount = children.length;
  return children.map(v => ({
    vault: v
  }));
}

async function () {
  showModal('Modal1');
  if (tbl_wallets.tableData.length < 1) {
    closeModal('Modal1');
    return [];
  }
  if (tbl_wallets.selectedRowIndex < 0) {
    closeModal('Modal1');
    return [];
  }
  if (tbl_wallets.selectedRowIndices < 0) {
    if (appsmith.URL.queryParams.vaultId != undefined && appsmith.URL.queryParams.vaultId != null) {
      vaultsApi.dataPath = ("/custom/vstorage/data/published.vaultFactory.managers.manager0.vaults.").concat(appsmith.URL.queryParams.vaultId);
    }
  } else {
    vaultsApi.dataPath = ("/custom/vstorage/data/published.vaultFactory.managers.manager0.vaults.").concat(tbl_wallets.selectedRow.vault);
  }
  let response = await RpcGetWalletDataAPI.run();
  closeModal('Modal1');
  vaultsApi.height = response.result.response.height;
  let data = response.result && response.result.response && response.result.response.value && JSON.parse(atob(response.result.response.value));
  let result = JSON.parse(data.value).values.map(v => ({
    value: vaultsApi.cleanTxt(v)
  })).map(kv => ({
    "value": kv.value.body
  }));
  return result;
}

async function () {
  if (appsmith.URL.queryParams.vaultId != undefined && appsmith.URL.queryParams.vaultId != null) {
    searchInput.setValue(appsmith.URL.queryParams.vaultId);
  }
  if (appsmith.URL.queryParams.network != undefined && appsmith.URL.queryParams.network != null) {
    networkInput.setSelectedOption(appsmith.URL.queryParams.network);
  }
  vaultsApi.getRpcWalletData();
}
